import {useState, useEffect} from 'react'
import axios from 'axios'

function Form(){

    const [name,setName] =useState();
    const [email, setEmail] = useState();
    const [arr, setArr] = useState([]);

    useEffect(()=>{
        axios.get("http://127.0.0.1:8000/api/sample_api").then((res)=>{
           setArr(res.data.data);
        })
    },[]);
    
    const submitForm = () =>{
        let getData = new FormData();
        getData.append('name', name);
        getData.append('email', email);
        axios({
            method:'POST',
            url:"http://127.0.0.1:8000/api/sample_api/send",
            data: getData
        }).then((response)=>{
            console.log(response.data.data);
            axios.get("http://127.0.0.1:8000/api/sample_api").then((res)=>{
                setArr(res.data.data);
             })
          
        })

    }

    const updateForm = (e) =>{
        //e.currentTarget.id

        let getData = new FormData();
        getData.append('name', name);
        getData.append('email', email);
        getData.append('id', e.currentTarget.id);
        axios({
            method:'POST',
            url:"http://127.0.0.1:8000/api/sample_api/edit/" +  e.currentTarget.id,
            data: getData
        }).then((response)=>{
            console.log(response.data.data);
            axios.get("http://127.0.0.1:8000/api/sample_api").then((res)=>{
                setArr(res.data.data);
             })
        })
            }

    const deleBtn = (e) =>{
        let getData = new FormData();
        getData.append('id', e.currentTarget.id);
        axios({
            method:'POST',
            url:"http://127.0.0.1:8000/api/sample_api/delete/" +  e.currentTarget.title,
            data: getData
        }).then((response)=>{
            console.log(response.data.data);
            axios.get("http://127.0.0.1:8000/api/sample_api").then((res)=>{
                setArr(res.data.data);
             })
        })
    }
            
        return(
        <>
        <form>
            Name: <input type="text" name="name" id="name" onChange={(e)=> setName(e.target.value)}/> <br/>
            Email: <input type="text" name="email" id="email" onChange={(e)=> setEmail(e.target.value)}/> <br/>
            <button type="button" onClick={submitForm}>Submit</button>
        </form>
        <hr/>
        {arr.map((item)=>{
            return(
                <>
                <tr>
                <td>
                    <input type="text" name="ename" id="ename" defaultValue={item.name} onChange={(e)=> setName(e.target.value)}/>
                </td>
                <td>
                    <input type="text" name="ename" id="ename" defaultValue={item.email} onChange={(e)=> setEmail(e.target.value)}/>
                </td>
                <td>
                    <button type="button" id={item.id} onClick={updateForm}>Update</button>
                    <button type="button" title={item.id} onClick={deleBtn}>Delete</button>
                </td>
                </tr>
                </>
            )
        })}
        </>
    )
}

export default Form;
